import React, { useState, useEffect } from 'react';
import { 
  Container, 
  Typography, 
  Box, 
  Grid, 
  Card, 
  CardContent, 
  CardMedia,
  Button,
  Paper
} from '@mui/material';
import { useNavigate } from 'react-router-dom';

function Home() {
  const navigate = useNavigate();
  const [recentActivity, setRecentActivity] = useState([]);
  const [topReaders, setTopReaders] = useState([]);
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    const checkAuthStatus = () => {
      const token = localStorage.getItem('token');
      setIsLoggedIn(!!token);
    };

    checkAuthStatus();
    fetchRecentActivity();
    fetchTopReaders();
  }, []);

  const fetchRecentActivity = async () => {
    try {
      const response = await fetch('http://localhost:3001/api/books');
      const books = await response.json();
      const activity = books
        .flatMap(book => book.drawings?.map(drawing => ({
          ...drawing,
          bookTitle: book.title
        })) || [])
        .sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt))
        .slice(0, 6);
      setRecentActivity(activity);
    } catch (error) {
      console.error('Error fetching activity:', error);
    }
  };

  const fetchTopReaders = async () => {
    try {
      // Updated to use the correct endpoint
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:3001/api/auth/users/check', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      const users = await response.json();
      // Sort users by points or another metric if available
      const sortedUsers = users.sort((a, b) => (b.points || 0) - (a.points || 0)).slice(0, 5);
      setTopReaders(sortedUsers);
    } catch (error) {
      console.error('Error fetching top readers:', error);
    }
  };

  return (
    <Container maxWidth="lg">
      {/* Rest of your JSX remains the same */}
      <Box
        sx={{
          pt: 8,
          pb: 6,
          textAlign: 'center',
          bgcolor: 'background.paper',
          borderRadius: 2,
          mb: 4
        }}
      >
        <Typography
          component="h1"
          variant="h2"
          color="text.primary"
          gutterBottom
        >
          Welcome to Reading Community!
        </Typography>
        <Typography variant="h5" color="text.secondary" paragraph>
          Share your reading journey through art! Read books, create drawings,
          and join a community of young readers and artists.
        </Typography>
        <Box sx={{ mt: 4 }}>
          <Button
            variant="contained"
            color="primary"
            size="large"
            onClick={() => navigate('/books')}
            sx={{ mr: 2 }}
          >
            Browse Books
          </Button>
          {!isLoggedIn && (
            <Button
              variant="outlined"
              color="primary"
              size="large"
              onClick={() => navigate('/register')}
            >
              Join Now
            </Button>
          )}
        </Box>
      </Box>

      {/* Recent Activity */}
      <Typography variant="h4" gutterBottom sx={{ mt: 6 }}>
        Recent Drawings
      </Typography>
      <Grid container spacing={3}>
        {recentActivity.map((activity, index) => (
          <Grid item xs={12} sm={6} md={4} key={index}>
            <Card>
              <CardMedia
                component="img"
                height="200"
                image={activity.imageUrl}
                alt={`Drawing for ${activity.bookTitle}`}
              />
              <CardContent>
                <Typography variant="subtitle1" gutterBottom>
                  {activity.bookTitle}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  by {activity.username}
                </Typography>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>

      {/* How It Works */}
      <Paper sx={{ p: 4, mt: 6 }}>
        <Typography variant="h4" gutterBottom>
          How It Works
        </Typography>
        <Grid container spacing={4}>
          <Grid item xs={12} md={4}>
            <Box textAlign="center">
              <Typography variant="h6" gutterBottom>
                1. Read a Book
              </Typography>
              <Typography>
                Choose from our collection of age-appropriate books
              </Typography>
            </Box>
          </Grid>
          <Grid item xs={12} md={4}>
            <Box textAlign="center">
              <Typography variant="h6" gutterBottom>
                2. Draw Your Favorite Scene
              </Typography>
              <Typography>
                Share your interpretation through artwork
              </Typography>
            </Box>
          </Grid>
          <Grid item xs={12} md={4}>
            <Box textAlign="center">
              <Typography variant="h6" gutterBottom>
                3. Earn Points & Share
              </Typography>
              <Typography>
                Get recognition and interact with other readers
              </Typography>
            </Box>
          </Grid>
        </Grid>
      </Paper>
    </Container>
  );
}

export default Home;
