// frontend/src/components/VocabularyQuiz.jsx

import React, { useState, useEffect } from 'react';
import { 
  Box,
  Card,
  Typography,
  Button,
  CircularProgress,
  RadioGroup,
  FormControlLabel,
  Radio,
  Alert
} from '@mui/material';
import vocabularyService from '../services/vocabularyService';

const VocabularyQuiz = ({ bookId }) => {
  console.log('VocabularyQuiz rendered with bookId:', bookId);
  const [questions, setQuestions] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [selectedAnswer, setSelectedAnswer] = useState('');
  const [score, setScore] = useState(0);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchQuiz = async () => {
      try {
        console.log('Fetching quiz for bookId:', bookId);
        const quizData = await vocabularyService.getQuizForBook(bookId);
        console.log('Quiz data received:', quizData);
        setQuestions(quizData.questions);
      } catch (err) {
        console.error('Error fetching quiz:', err);
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    if (bookId) {
      fetchQuiz();
    }
  }, [bookId]);

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="200px">
        <CircularProgress />
      </Box>
    );
  }

  if (error) {
    return (
      <Alert severity="error">{error}</Alert>
    );
  }

  if (!questions || questions.length === 0) {
    return (
      <Alert severity="info">
        No vocabulary words found for this book. Add some vocabulary words to take a quiz!
      </Alert>
    );
  }

  const currentQuestion = questions[currentQuestionIndex];

  return (
    <Box>
      <Card sx={{ p: 3 }}>
        <Typography variant="h6" gutterBottom>
          Question {currentQuestionIndex + 1} of {questions.length}
        </Typography>

        <Typography variant="body1" gutterBottom>
          {currentQuestion?.question}
        </Typography>

        <RadioGroup
          value={selectedAnswer}
          onChange={(e) => setSelectedAnswer(e.target.value)}
          sx={{ my: 2 }}
        >
          {currentQuestion?.options?.map((option, index) => (
            <FormControlLabel
              key={index}
              value={option}
              control={<Radio />}
              label={option}
              sx={{ mb: 1 }}
            />
          ))}
        </RadioGroup>

        <Button
          variant="contained"
          onClick={() => {
            // Handle answer submission
            console.log('Selected answer:', selectedAnswer);
            // Add your answer submission logic here
          }}
          disabled={!selectedAnswer}
          fullWidth
        >
          Submit Answer
        </Button>
      </Card>
    </Box>
  );
};

export default VocabularyQuiz;
