// frontend/src/components/vocabulary/VocabularyInterface.js

import React, { useState, useEffect } from 'react';
import vocabularyService from '../../services/vocabularyService';
import { useNavigate } from 'react-router-dom';

// Import your shadcn components
import { Card } from '../../components/ui/card';
import { Button } from '../../components/ui/button';
import { Textarea } from '../../components/ui/textarea';
import { Alert } from '../../components/ui/alert';

const VocabularyInterface = ({ bookId }) => {
  const navigate = useNavigate();
  const [passage, setPassage] = useState('');
  const [selectedWord, setSelectedWord] = useState('');
  const [wordDetails, setWordDetails] = useState(null);
  const [savedWords, setSavedWords] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // Load saved words for this book
  useEffect(() => {
    if (bookId) {
      loadSavedWords();
    }
  }, [bookId]);

  const loadSavedWords = async () => {
    try {
      const words = await vocabularyService.getBookVocabulary(bookId);
      setSavedWords(words);
    } catch (err) {
      setError('Failed to load vocabulary words');
      console.error(err);
    }
  };

  const handleTextSelection = () => {
    const selection = window.getSelection();
    const selectedText = selection.toString().trim();

    if (selectedText && selectedText.split(' ').length === 1) {
      setSelectedWord(selectedText);
      handleWordLookup(selectedText);
    }
  };

  const handleWordLookup = async (word) => {
    setLoading(true);
    try {
      // For now, we'll create a placeholder for etymology data
      // Later, we can integrate with an etymology API
      const etymologyData = {
        root: "Example root",
        originLanguage: "Latin",
        meaning: "Original meaning",
        evolution: [
          { period: "Ancient", form: word },
          { period: "Modern", form: word }
        ]
      };

      setWordDetails({
        word,
        etymology: etymologyData
      });
    } catch (err) {
      setError('Failed to look up word details');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleSaveWord = async () => {
    if (!selectedWord || !wordDetails) return;

    try {
      const wordData = {
        word: selectedWord,
        bookId,
        context: {
          passage: passage,
          pageNumber: null // You can add a page number input if needed
        },
        etymology: wordDetails.etymology
      };

      await vocabularyService.addVocabularyWord(wordData);
      await loadSavedWords(); // Refresh the list
      setSelectedWord('');
      setWordDetails(null);
    } catch (err) {
      setError('Failed to save word');
      console.error(err);
    }
  };

  return (
    <div className="max-w-4xl mx-auto p-4 space-y-6">
      {error && (
        <Alert variant="destructive">
          {error}
        </Alert>
      )}

      <Card className="p-6">
        <h2 className="text-2xl font-bold mb-4">Vocabulary Helper</h2>

        <div className="space-y-4">
          <div>
            <label className="block text-sm font-medium mb-2">
              Enter Book Passage
            </label>
            <Textarea
              value={passage}
              onChange={(e) => setPassage(e.target.value)}
              placeholder="Enter a passage from the book..."
              className="min-h-[200px]"
            />
          </div>

          {passage && (
            <div>
              <label className="block text-sm font-medium mb-2">
                Select Words for Analysis
              </label>
              <div 
                className="p-4 border rounded-lg bg-gray-50"
                onMouseUp={handleTextSelection}
              >
                {passage}
              </div>
            </div>
          )}

          {selectedWord && wordDetails && (
            <Card className="p-4">
              <h3 className="text-xl font-semibold mb-3">Word Analysis: {selectedWord}</h3>

              <div className="space-y-3">
                <div>
                  <h4 className="font-medium">Etymology:</h4>
                  <p>Root: {wordDetails.etymology.root}</p>
                  <p>Origin: {wordDetails.etymology.originLanguage}</p>
                  <p>Original Meaning: {wordDetails.etymology.meaning}</p>
                </div>

                <div>
                  <h4 className="font-medium">Evolution:</h4>
                  {wordDetails.etymology.evolution.map((stage, index) => (
                    <div key={index} className="ml-4">
                      {stage.period}: {stage.form}
                    </div>
                  ))}
                </div>

                <Button
                  onClick={handleSaveWord}
                  disabled={loading}
                  className="w-full"
                >
                  Save Word for Review
                </Button>
              </div>
            </Card>
          )}
        </div>
      </Card>

      {savedWords.length > 0 && (
        <Card className="p-6">
          <h3 className="text-xl font-bold mb-4">Saved Words</h3>
          <div className="space-y-3">
            {savedWords.map((word, index) => (
              <div 
                key={index}
                className="p-3 border rounded-lg hover:bg-gray-50"
              >
                <h4 className="font-medium">{word.word}</h4>
                <p className="text-sm text-gray-600">
                  Root: {word.etymology.root}
                </p>
                {word.context?.passage && (
                  <p className="text-sm text-gray-500 mt-1">
                    Context: "{word.context.passage}"
                  </p>
                )}
              </div>
            ))}
          </div>
        </Card>
      )}
    </div>
  );
};

export default VocabularyInterface;
