import React, { useState } from 'react';
import { 
  Card, 
  CardContent, 
  CardMedia, 
  IconButton, 
  Typography, 
  Box,
  TextField,
  //Button,
  Collapse
} from '@mui/material';
import { 
  Favorite, 
  FavoriteBorder, 
  ChatBubbleOutline,
  Send
} from '@mui/icons-material';

function DrawingCard({ drawing, bookId, onLikeUpdate }) {
  const [likeCount, setLikeCount] = useState(drawing.likes?.length || 0);
  const [isLiked, setIsLiked] = useState(drawing.likes?.includes(localStorage.getItem('userId')));
  const [showComments, setShowComments] = useState(false);
  const [newComment, setNewComment] = useState('');

  const handleLike = async () => {
    try {
      const endpoint = isLiked ? 'unlike' : 'like';
      const response = await fetch(
        `http://localhost:3001/api/books/${bookId}/drawings/${drawing._id}/${endpoint}`,
        {
          method: 'POST',
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`
          }
        }
      );

      if (response.ok) {
        const newIsLiked = !isLiked;
        setLikeCount(prev => isLiked ? prev - 1 : prev + 1);
        setIsLiked(!isLiked);
        onLikeUpdate();
      }
    } catch (error) {
      console.error('Error handling like:', error);
    }
  };

  const handleComment = async (e) => {
    e.preventDefault();
    if (!newComment.trim()) return;

    try {
      const response = await fetch(
        `http://localhost:3001/api/books/${bookId}/drawings/${drawing._id}/comments`,
        {
          method: 'POST',
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`,
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({ content: newComment })
        }
      );

      if (response.ok) {
        const data = await response.json();
        //update local state immediately
        drawing.comments = [...(drawing.comments || []), data.comment];
        setNewComment('');
        onLikeUpdate(); // This will refresh the whole book data, including comments
      }
    } catch (error) {
      console.error('Error posting comment:', error);
    }
  };

  return (
    <Card sx={{ mb: 2 }}>
      <CardMedia
        component="img"
        image={drawing.imageUrl}
        alt="User Drawing"
        sx={{ height: 'auto', maxHeight: '400px', objectFit: 'contain' }}
      />
      <CardContent>
        <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
          <Box sx={{ display: 'flex', alignItems: 'center' }}>
            <IconButton 
              size="small" 
              onClick={handleLike}
              color={isLiked ? "error" : "default"}
            >
              {isLiked ? <Favorite /> : <FavoriteBorder />}
            </IconButton>
            <Typography variant="body2">
              {likeCount}
            </Typography>
          </Box>
          <Box sx={{ display: 'flex', alignItems: 'center' }}>
            <IconButton 
              size="small" 
              onClick={() => setShowComments(!showComments)}
            >
              <ChatBubbleOutline />
            </IconButton>
            <Typography variant="body2">
              {drawing.comments?.length || 0}
            </Typography>
          </Box>
        </Box>

        <Collapse in={showComments}>
          <Box sx={{ mt: 2 }}>
            {/* Comments list */}
            {drawing.comments?.map((comment, index) => (
              <Box key={index} sx={{ mb: 1, p: 1, bgcolor: 'grey.100', borderRadius: 1 }}>
                <Typography variant="subtitle2" color="primary">
                  {comment.username}
                </Typography>
                <Typography variant="body2">
                  {comment.content}
                </Typography>
              </Box>
            ))}

            {/* Comment form */}
            <Box 
              component="form" 
              onSubmit={handleComment}
              sx={{ 
                mt: 2, 
                display: 'flex', 
                gap: 1 
              }}
            >
              <TextField
                size="small"
                placeholder="Add a comment..."
                value={newComment}
                onChange={(e) => setNewComment(e.target.value)}
                fullWidth
                multiline
                maxRows={3}
              />
              <IconButton 
                type="submit" 
                color="primary"
                disabled={!newComment.trim()}
              >
                <Send />
              </IconButton>
            </Box>
          </Box>
        </Collapse>
      </CardContent>
    </Card>
  );
}

export default DrawingCard;
