// frontend/src/pages/Home.js
import React, { useState, useEffect } from 'react';
import { 
  Container, 
  Typography, 
  Box, 
  Grid, 
  Card, 
  CardContent, 
  CardMedia,
  Button,
  Paper
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import TreeNavigation from '../components/TreeNavigation';

function Home() {
  const navigate = useNavigate();
  const [recentActivity, setRecentActivity] = useState([]);
  const [topReaders, setTopReaders] = useState([]);
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    const checkAuthStatus = () => {
      const token = localStorage.getItem('token');
      setIsLoggedIn(!!token);
    };

    checkAuthStatus();
    fetchRecentActivity();
    fetchTopReaders();
  }, []);

  const fetchRecentActivity = async () => {
    try {
      const response = await fetch('http://localhost:3001/api/books');
      const books = await response.json();
      const activity = books
        .flatMap(book => book.drawings?.map(drawing => ({
          ...drawing,
          bookTitle: book.title
        })) || [])
        .sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt))
        .slice(0, 6);
      setRecentActivity(activity);
    } catch (error) {
      console.error('Error fetching activity:', error);
    }
  };

  const fetchTopReaders = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:3001/api/auth/users/check', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      const users = await response.json();
      const sortedUsers = users.sort((a, b) => (b.points || 0) - (a.points || 0)).slice(0, 5);
      setTopReaders(sortedUsers);
    } catch (error) {
      console.error('Error fetching top readers:', error);
    }
  };

  return (
    <div className="min-h-screen">
      <TreeNavigation />
      <Container maxWidth="lg">
        <Box sx={{ mt: 6 }}>
          <Typography variant="h4" gutterBottom>
            Recent Drawings
          </Typography>
          <Grid container spacing={3}>
            {recentActivity.map((activity, index) => (
              <Grid item xs={12} sm={6} md={4} key={index}>
                <Card>
                  <CardMedia
                    component="img"
                    height="200"
                    image={activity.imageUrl}
                    alt={`Drawing for ${activity.bookTitle}`}
                  />
                  <CardContent>
                    <Typography variant="subtitle1" gutterBottom>
                      {activity.bookTitle}
                    </Typography>
                  </CardContent>
                </Card>
              </Grid>
            ))}
          </Grid>
        </Box>
      </Container>
    </div>
  );
}

export default Home;
