import React from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';

const TreeNavigation = () => {
  const navItems = [
    { path: '/about', label: 'About Us', x: 120, y: 180 },
    { path: '/contact', label: 'Contact', x: -120, y: 220 },
    { path: '/resources', label: 'Resources', x: 180, y: 260 },
    { path: '/little-library', label: 'Little Free Library', x: -180, y: 300 }
  ];

  return (
    <div className="relative min-h-screen w-full bg-gradient-to-b from-green-50 to-green-100">
      {/* Treehouse Background Image */}
      <div 
        className="absolute inset-0 bg-cover bg-center opacity-90"
        style={{ 
          backgroundImage: `url('/treehouse-bg.jpg')`,
          filter: 'brightness(0.9)'
        }}
      />

      {/* Content Overlay */}
      <div className="relative z-10 pt-16 px-4">
        <h1 className="text-4xl md:text-6xl font-bold text-center text-amber-900 mb-8">
          Brooks'Books
        </h1>
        <p className="text-xl text-center text-amber-800 max-w-2xl mx-auto mb-12">
          Where digital reading meets treehouse libraries, creating a unique community of book lovers.
        </p>

        {/* SVG Tree with Interactive Branches */}
        <svg 
          viewBox="0 0 800 600" 
          className="w-full max-w-4xl mx-auto"
        >
          {/* Tree Trunk */}
          <path
            d="M380 500 Q400 400 400 300 Q400 200 380 100"
            className="stroke-amber-900"
            strokeWidth="40"
            fill="none"
          />

          {/* Branches and Navigation Links */}
          {navItems.map((item, index) => (
            <g key={index}>
              {/* Branch */}
              <motion.path
                d={`M400 ${300 + item.y/2} Q${400 + item.x/2} ${300 + item.y/3} ${400 + item.x} ${300 + item.y}`}
                className="stroke-amber-800"
                strokeWidth="20"
                fill="none"
                initial={{ pathLength: 0 }}
                animate={{ pathLength: 1 }}
                transition={{ duration: 1, delay: index * 0.2 }}
              />

              {/* Interactive Link */}
              <motion.g
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ delay: 0.5 + index * 0.2 }}
                whileHover={{ scale: 1.1 }}
              >
                <Link to={item.path}>
                  <circle
                    cx={400 + item.x}
                    cy={300 + item.y}
                    r="40"
                    className="fill-green-100 stroke-amber-900 stroke-2 cursor-pointer transition-all duration-300 hover:fill-green-200"
                  />
                  <text
                    x={400 + item.x}
                    y={300 + item.y}
                    textAnchor="middle"
                    dominantBaseline="middle"
                    className="text-sm font-semibold fill-amber-900 pointer-events-none"
                  >
                    {item.label}
                  </text>
                </Link>
              </motion.g>
            </g>
          ))}

          {/* Leaves/Foliage */}
          <circle cx="400" cy="150" r="100" className="fill-green-800 opacity-80" />
          <circle cx="300" cy="200" r="80" className="fill-green-700 opacity-80" />
          <circle cx="500" cy="200" r="90" className="fill-green-800 opacity-80" />
          <circle cx="380" cy="250" r="70" className="fill-green-700 opacity-80" />
          <circle cx="450" cy="180" r="85" className="fill-green-800 opacity-80" />
        </svg>
      </div>
    </div>
  );
};

export default TreeNavigation;
