// frontend/src/services/etymologyService.js

const PROTO_INDO_EUROPEAN_ROOTS = {
  '*bher-': {
    meaning: 'to carry, bring',
    descendants: {
      latin: ['ferre (to bear)', 'fertile', 'transfer'],
      greek: ['pherein (to carry)', 'phosphorus (light-bearing)'],
      germanic: ['bear', 'birth'],
      sanskrit: ['bharati (he carries)']
    }
  },
  '*weid-': {
    meaning: 'to see, to know',
    descendants: {
      latin: ['videre (to see)', 'vision', 'video'],
      greek: ['idein (to see)', 'idea'],
      germanic: ['wit', 'wise'],
      sanskrit: ['veda (knowledge)']
    }
  },
  '*dhē-': {
    meaning: 'to set, put, place',
    descendants: {
      latin: ['facere (to do)', 'fact'],
      greek: ['thesis (placing)', 'theme'],
      germanic: ['do', 'deed'],
      sanskrit: ['dhātu (element)']
    }
  },
  '*leuk-': {
    meaning: 'light, brightness',
    descendants: {
      latin: ['lux (light)', 'lucid', 'illuminate'],
      greek: ['leukos (white)', 'lychnos (lamp)'],
      germanic: ['light', 'lightning'],
      sanskrit: ['roca (bright)']
    }
  }
};

const LANGUAGE_FAMILIES = {
  GERMANIC: {
    timeline: [
      { period: 'Proto-Germanic', years: '500 BCE-200 CE' },
      { period: 'West Germanic', years: '200-500 CE' },
      { period: 'Old English', years: '500-1100 CE' },
      { period: 'Middle English', years: '1100-1500 CE' },
      { period: 'Early Modern English', years: '1500-1800 CE' },
      { period: 'Modern English', years: '1800-present' }
    ],
    soundChanges: {
      'Proto-Germanic': 'Grimm\'s Law consonant shifts',
      'West Germanic': 'Loss of word-final -z',
      'Old English': 'I-mutation and breaking of vowels',
      'Middle English': 'Great Vowel Shift begins',
      'Early Modern English': 'Great Vowel Shift completes'
    }
  },
  ROMANCE: {
    timeline: [
      { period: 'Classical Latin', years: '75 BCE-3rd c. CE' },
      { period: 'Vulgar Latin', years: '3rd-8th c. CE' },
      { period: 'Old French', years: '842-1400 CE' },
      { period: 'Middle French', years: '1400-1600 CE' },
      { period: 'Modern French', years: '1600-present' }
    ],
    soundChanges: {
      'Vulgar Latin': 'Loss of final consonants',
      'Old French': 'Lenition of intervocalic consonants',
      'Middle French': 'Loss of final -e in many words'
    }
  }
};

const SEMANTIC_DEVELOPMENTS = {
  'nice': [
    'From Latin nescius "ignorant"',
    'Old French nice "silly, simple"',
    'Middle English "foolish, stupid"',
    'Gradually shifted to "pleasing, agreeable"',
    'Modern English "pleasant, kind"'
  ],
  'silly': [
    'Old English gesælig "happy, fortunate, blessed"',
    'Middle English "innocent, harmless, pitiable"',
    'Later "weak, feeble, frail"',
    'Finally "foolish, simple, ridiculous"'
  ]
};

const analyzeWord = (word) => {
  // Convert word to lowercase for matching
  const wordLower = word.toLowerCase();

  // Try to find PIE root
  let pieRoot = null;
  for (const [root, info] of Object.entries(PROTO_INDO_EUROPEAN_ROOTS)) {
    if (Object.values(info.descendants).flat().some(desc => 
      desc.toLowerCase().includes(wordLower) ||
      wordLower.includes(desc.toLowerCase().split(' ')[0])
    )) {
      pieRoot = { root, ...info };
      break;
    }
  }

  // Determine language family and evolution path
  let evolution = [];
  let modernCognates = {};
  let semanticDevelopment = null;

  // Check for Germanic words
  if (isGermanicWord(wordLower)) {
    evolution = buildEvolutionPath(wordLower, 'GERMANIC');
    modernCognates = getGermanicCognates(wordLower);
  } 
  // Check for Romance words
  else if (isRomanceWord(wordLower)) {
    evolution = buildEvolutionPath(wordLower, 'ROMANCE');
    modernCognates = getRomanceCognates(wordLower);
  }

  // Check for semantic development history
  if (SEMANTIC_DEVELOPMENTS[wordLower]) {
    semanticDevelopment = SEMANTIC_DEVELOPMENTS[wordLower];
  }

  return {
    word,
    pie: pieRoot,
    evolution,
    modernCognates,
    semanticDevelopment
  };
};

// Helper functions
const isGermanicWord = (word) => {
  // Basic check for Germanic origin (can be enhanced)
  const germanicPatterns = [
    'th', 'gh', 'ght', 'ing', 'ness', 'ship', 'hood'
  ];
  return germanicPatterns.some(pattern => word.includes(pattern));
};

const isRomanceWord = (word) => {
  // Basic check for Romance origin (can be enhanced)
  const romancePatterns = [
    'tion', 'sion', 'ment', 'ance', 'ence', 'ify', 'ous'
  ];
  return romancePatterns.some(pattern => word.includes(pattern));
};

const buildEvolutionPath = (word, family) => {
  const timeline = LANGUAGE_FAMILIES[family].timeline;
  const soundChanges = LANGUAGE_FAMILIES[family].soundChanges;

  return timeline.map(stage => ({
    ...stage,
    changes: soundChanges[stage.period],
    form: getHistoricalForm(word, stage.period, family)
  }));
};

const getHistoricalForm = (word, period, family) => {
  // This is a simplified example - in a real implementation,
  // you would have a database of historical forms
  if (family === 'GERMANIC') {
    const historicalForms = {
      'light': {
        'Proto-Germanic': '*leuhtą',
        'West Germanic': '*lioht',
        'Old English': 'lēoht',
        'Middle English': 'light',
        'Early Modern English': 'light',
        'Modern English': 'light'
      }
      // Add more words...
    };
    return historicalForms[word]?.[period] || word;
  }
  return word; // Fallback
};

const getGermanicCognates = (word) => {
  // Example cognates - in a real implementation,
  // you would have a comprehensive database
  const cognates = {
    'light': {
      'German': 'Licht',
      'Dutch': 'licht',
      'Swedish': 'ljus',
      'Danish': 'lys'
    }
    // Add more words...
  };
  return cognates[word] || {};
};

const getRomanceCognates = (word) => {
  // Example cognates
  const cognates = {
    'nature': {
      'French': 'nature',
      'Spanish': 'naturaleza',
      'Italian': 'natura',
      'Portuguese': 'natureza'
    }
    // Add more words...
  };
  return cognates[word] || {};
};

export {
  analyzeWord,
  PROTO_INDO_EUROPEAN_ROOTS,
  LANGUAGE_FAMILIES
};
