// frontend/src/services/vocabularyService.js

const API_URL = 'http://localhost:3001/api';

class VocabularyService {
  constructor() {
    this.token = localStorage.getItem('token');
  }

  async addVocabularyWord(wordData) {
      try {
        const token = localStorage.getItem('token');
        const response = await fetch('http://localhost:3001/api/vocabulary', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`
          },
          body: JSON.stringify({
            ...wordData,
            createdBy: localStorage.getItem('userId') // Add this line
          })
        });

        if (!response.ok) {
          throw new Error('Failed to add vocabulary word');
        }

        return await response.json();
      } catch (error) {
        console.error('Error adding vocabulary word:', error);
        throw error;
      }
    }

  async getBookVocabulary(bookId) {
    try {
      const response = await fetch(`${API_URL}/vocabulary/book/${bookId}`, {
        headers: {
          'Authorization': `Bearer ${this.token}`
        }
      });

      if (!response.ok) {
        throw new Error('Failed to fetch vocabulary');
      }

      return await response.json();
    } catch (error) {
      console.error('Error fetching vocabulary:', error);
      throw error;
    }
  }

  async updateVocabularyWord(id, updateData) {
    try {
      const response = await fetch(`${API_URL}/vocabulary/${id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${this.token}`
        },
        body: JSON.stringify(updateData)
      });

      if (!response.ok) {
        throw new Error('Failed to update vocabulary word');
      }

      return await response.json();
    } catch (error) {
      console.error('Error updating vocabulary word:', error);
      throw error;
    }
  }

  async deleteVocabularyWord(id) {
    try {
      const response = await fetch(`${API_URL}/vocabulary/${id}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${this.token}`
        }
      });

      if (!response.ok) {
        throw new Error('Failed to delete vocabulary word');
      }

      return await response.json();
    } catch (error) {
      console.error('Error deleting vocabulary word:', error);
      throw error;
    }
  }
}

export default new VocabularyService();
