import React from 'react';
import { Card } from './ui/card';

const EtymologyTree = ({ etymologyData }) => {
  if (!etymologyData) return null;

  return (
    <div className="w-full">
      <Card className="p-6 bg-gradient-to-br from-amber-50 to-amber-100">
        <div className="mb-4">
          <h3 className="text-2xl font-bold text-amber-900">
            Etymology Tree: {etymologyData.word}
          </h3>
        </div>

        <div className="relative">
          {/* Root Word Section */}
          <div className="flex justify-center mb-8">
            <div className="bg-amber-800 text-white px-4 py-2 rounded-lg shadow-lg">
              <div className="text-sm font-semibold">Root</div>
              <div>{etymologyData.root}</div>
              <div className="text-xs opacity-75">{etymologyData.origin}</div>
            </div>
          </div>

          {/* Evolution Path */}
          {etymologyData.evolutionPath?.stages && (
            <div className="space-y-8">
              {etymologyData.evolutionPath.stages.map((stage, index) => (
                <div key={index} className="flex flex-col items-center">
                  {/* Connector Line */}
                  {index > 0 && (
                    <div className="h-8 w-0.5 bg-amber-300 -mt-8"></div>
                  )}

                  {/* Stage */}
                  <div className="bg-amber-100 px-4 py-2 rounded-lg shadow border border-amber-200">
                    <div className="text-sm font-semibold text-amber-900">{stage}</div>
                    {etymologyData.evolutionPath.examples?.[stage] && (
                      <div className="text-amber-700">
                        {etymologyData.evolutionPath.examples[stage]}
                      </div>
                    )}
                  </div>
                </div>
              ))}
            </div>
          )}

          {/* Derivatives */}
          {etymologyData.derivatives && etymologyData.derivatives.length > 0 && (
            <div className="mt-8">
              <h4 className="text-lg font-semibold text-amber-900 mb-2">Related Words</h4>
              <div className="flex flex-wrap gap-2">
                {etymologyData.derivatives.map((word, index) => (
                  <span 
                    key={index}
                    className="bg-amber-50 text-amber-800 px-3 py-1 rounded-full text-sm border border-amber-200"
                  >
                    {word}
                  </span>
                ))}
              </div>
            </div>
          )}
        </div>
      </Card>
    </div>
  );
};

export default EtymologyTree;
