import React, { useState, useEffect } from 'react';
import { Container, Grid, Card, CardContent, CardMedia } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import TreeNavigation from '../components/TreeNavigation';
import { motion } from 'framer-motion';

function Home() {
//  const navigate = useNavigate();
  const [recentActivity, setRecentActivity] = useState([]);
  const [topReaders, setTopReaders] = useState([]);
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    const checkAuthStatus = () => {
      const token = localStorage.getItem('token');
      setIsLoggedIn(!!token);
    };

    checkAuthStatus();
    fetchRecentActivity();
    fetchTopReaders();
  }, []);

  const fetchRecentActivity = async () => {
    try {
      const response = await fetch('http://localhost:3001/api/books');
      const books = await response.json();
      const activity = books
        .flatMap(book => book.drawings?.map(drawing => ({
          ...drawing,
          bookTitle: book.title
        })) || [])
        .sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt))
        .slice(0, 6);
      setRecentActivity(activity);
    } catch (error) {
      console.error('Error fetching activity:', error);
    }
  };

  const fetchTopReaders = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:3001/api/auth/users/check', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      const users = await response.json();
      const sortedUsers = users.sort((a, b) => (b.points || 0) - (a.points || 0)).slice(0, 5);
      setTopReaders(sortedUsers);
    } catch (error) {
      console.error('Error fetching top readers:', error);
    }
  };

  return (
    <div className="min-h-screen bg-gray-900">
      <TreeNavigation />
      <Container maxWidth="lg" className="relative z-10">
        <motion.div 
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 1 }}
          className="mt-8"
        >
          <h2 className="text-2xl font-bold text-blue-400 mb-6 text-center">
            Recent Explorations
          </h2>
          <Grid container spacing={3}>
            {recentActivity.map((activity, index) => (
              <Grid item xs={12} sm={6} md={4} key={index}>
                <motion.div
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ delay: index * 0.1 + 1.2 }}
                >
                  <Card className="bg-gray-800 text-blue-300 hover:bg-gray-700 transition-colors">
                    <CardMedia
                      component="img"
                      height="200"
                      image={activity.imageUrl}
                      alt={`Drawing for ${activity.bookTitle}`}
                      className="opacity-80 hover:opacity-100 transition-opacity"
                    />
                    <CardContent>
                      <h3 className="text-lg font-semibold text-blue-400">
                        {activity.bookTitle}
                      </h3>
                      <p className="text-blue-300 text-sm mt-2">
                        Explored on {new Date(activity.createdAt).toLocaleDateString()}
                      </p>
                    </CardContent>
                  </Card>
                </motion.div>
              </Grid>
            ))}
          </Grid>
        </motion.div>
      </Container>
    </div>
  );
}

export default Home;
