import React, { useState, useEffect } from 'react';
import { Container, Typography, Box, Paper } from '@mui/material';
import BookGraph from '../components/BookGraph';
import { useNavigate } from 'react-router-dom';

function BookExplorer() {
  const [graphData, setGraphData] = useState({ nodes: [], links: [] });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    fetchGraphData();
  }, []);

  const fetchGraphData = async () => {
      try {
          setLoading(true);
          const response = await fetch('http://localhost:3001/api/recommendations/graph');
          if (!response.ok) {
              throw new Error('Failed to fetch graph data');
          }
          const data = await response.json();

          if (!data.nodes || !data.links) {
              console.error('Invalid graph data structure:', data);
              setError('Invalid graph data structure received');
              return;
          }

          // Filter out any invalid links
          const validNodeIds = new Set(data.nodes.map(node => node.id));
          const validLinks = data.links.filter(link => 
              validNodeIds.has(link.source) && validNodeIds.has(link.target)
          );

          setGraphData({
              nodes: data.nodes,
              links: validLinks
          });
      } catch (err) {
          console.error('Error fetching graph data:', err);
          setError(err.message);
      } finally {
          setLoading(false);
      }
  };

  const handleNodeClick = (node) => {
    if (node.type === 'Book') {
      navigate(`/books/${node.properties.id}`);
    }
  };

  if (loading) {
    return (
      <Container sx={{ mt: 4 }}>
        <Typography>Loading book network...</Typography>
      </Container>
    );
  }

  if (error) {
    return (
      <Container sx={{ mt: 4 }}>
        <Typography color="error">Error: {error}</Typography>
      </Container>
    );
  }

  return (
    <Container maxWidth="xl" sx={{ mt: 4, height: '90vh' }}>
      <Typography variant="h4" gutterBottom>
        Book Explorer
      </Typography>
      <Typography variant="subtitle1" gutterBottom>
        Click and drag nodes to explore connections between books, authors, genres, and tags.
      </Typography>

      <Paper elevation={3} sx={{ height: 'calc(100% - 100px)', overflow: 'hidden' }}>
        <BookGraph 
          graphData={graphData}
          onNodeClick={handleNodeClick}
        />
      </Paper>
    </Container>
  );
}

export default BookExplorer;
